def isPalin(s):
        palin = True
        for idx, i in enumerate(s):
            if s[idx] != s[len(s)-idx-1]:
                palin = False
        return palin
            
def longestPalindrome(s: str) -> str:
    longest = ""
    
    for idx, i in enumerate(s):
        for idx2, j in enumerate(s):
            curr_string = s[idx:idx2+1]
            if isPalin(curr_string):
                if len(curr_string) > len(longest):
                    longest = curr_string
    return longest

